			z[5], b = bits.Add64(z[5], qElementWord5, b)
			z[6], b = bits.Add64(z[6], qElementWord6, b)
			z[7], b = bits.Add64(z[7], qElementWord7, b)
			z[8], b = bits.Add64(z[8], qElementWord8, b)
			z[9], _ = bits.Add64(neg1, qElementWord9, b)
